import React from "react";
import { useEffect } from "react";
import KhaltiCheckout from "khalti-checkout-web";
import config from "./KhaltiConfig.js";


const Khalti = ({ orderDetails,history}) => {
  const { order } = orderDetails;

  config.productIdentity = order.orderItems[0].product;
  config.productName = order.orderItems[0].name;
  config.orderId = order._id;

  const checkout = new KhaltiCheckout(config);

  const handlePayment = () => {
    checkout.show({ amount: Number(order.totalPrice) * 100 });
    

  };

  useEffect(() => {
    const handlePaymentSuccess = (event) => {
      // Perform any necessary logic based on the event data
      // Redirect to the homepage after successful payment
      history.push('/');
    };

    // Listen for the payment-success event
    document.addEventListener('payment-success', handlePaymentSuccess);

    // Clean up the event listener
    return () => {
      document.removeEventListener('payment-success', handlePaymentSuccess);
    };
  }, [history]);

 

  return (
    <div>
      <button onClick={handlePayment}>Pay Via Khalti</button>
    </div>
  );
};

export default Khalti;
