import React, {useRef, useState, useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import { Form, Button, Row, Col, Toast } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'
import axios from "axios";

const OtpForm = ({location}) => {
    const [email, setEmail] = useState('');
    
  
    const submitHandler = (e) => {
      e.preventDefault();
      // TODO: Send password reset email using Nodemailer
    };
    const redirect = location.search ? location.search.split('=')[1] : '/'
  
    const emailRef= useRef();
    const sendOtp = async () => {
        try {
          const response = await axios.post('http://localhost:5000/api/users/email-send', { email: emailRef.current.value });
          console
          const { statusText, message } = response.data;
      
          if (statusText === 'Success') {
            Toast.success(message);
          } else {
            Toast.error(message);
          }
        } catch (error) {
          console.error(error);
        }
      }

    return (
        
      
        <FormContainer>
            <h1>Forget Password</h1>
    
      <Form onSubmit={submitHandler}>
        <Form.Group controlId='email'>
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type='email'
            placeholder='Enter email'
            value={email}
            ref={emailRef}
            onChange={(e) => setEmail(e.target.value)}
          ></Form.Control>
        </Form.Group>
  
        <Button type='submit' variant='primary' onClick={sendOtp}>
          Reset Password
        </Button>
      </Form>

      <Row className='py-3'>
        <Col>
          <Link to={redirect ? `/login?redirect=${redirect}` : '/login'}>
            Login
          </Link>
        </Col>
      </Row>

      </FormContainer>
    );
  };
  
  export default OtpForm;
