import React from "react";

import KhaltiCheckout from "khalti-checkout-web";
import config from "./KhaltiConfig.js";
import axios from "axios";



export default function Khalti({orderDetails}) {
    const {order} = orderDetails

    config.productIdentity = order.orderItems[0].product;
    config.productName = order.orderItems[0].name;
    config.orderId= order._id;
   
    
    const checkout = new KhaltiCheckout(config);
 
 
  
  const buttonStyles = {
    backgroundColor: "purple",
    padding: "10px",
    color: "white",
    cursor: "pointer",
    fontWeight: "bold",
    border: "1px solid white",
  };

  const handlePaymentSuccess = (payload) => {
    const data = {
      token: payload.token,
      amount: payload.amount,
    };

    axios.post("http://localhost:5000/api/orders/verifypayment", data)
      .then((response) => {
        console.log(response.data); // Verify status

        // Update the isPaid status in the database
        if (response.data.status === "success") {
          const orderId = response.data.order._id;

          axios.put(`http://localhost:5000/api/orders/${orderId}/markaspaid`)
            .then((response) => {
              console.log(response.data); // Updated order object
              // Perform any other actions after updating the isPaid status
            })
            .catch((error) => {
              console.log(error.message);
            });
        }
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  return (
    <div>
      <button
        onClick={() => checkout.show({ amount: Number(order.totalPrice)*100})}
        style={buttonStyles}
      >
        Pay Via Khalti
      </button>
    </div>
  );
}
